mapper.insert:
insert into tbprbnew (select #{startTime}, Base_station_name,
avg(${column})
from (select * from tbprb where Start_time >= #{startTime} and Start_time < #{endTime}) as A group by Base_station_name)

mapper.select:
select start_time, ${colName}
from tbprbnew
where Base_station_name = #{stationName} and start_time between #{startTime} and #{endTime}
order by start_time ASC
